	case NPIPE:
		writenode(n, sizeof(struct npipe), nodesize[n->type]);
		encodenodelist(n->npipe.cmdlist);
		break;
	case NREDIR:
	case NBACKGND:
	case NSUBSHELL:
		writenode(n, sizeof(struct nredir), nodesize[n->type]);
		encodenode(n->nredir.redirect);
		encodenode(n->nredir.n);
